import { useState, useEffect } from "react";
import ProductCard from "./ProductCard/ProductCard";
import Chevron from "./Chevron";
import { goLeft, goRight } from "./HelperFunctions";
const Carousel = (props) => {
  const [startIndex, setStartIndex] = useState(0);
  const [items, setItems] = useState([]);
  const [itemsToShow, setItemsToShow] = useState([]);
  const [count, setCount] = useState(0);
  const [disableChevronRight, setDisabledChevronRight] = useState(false);
  const [disableChevronLeft, setDisabledChevronLeft] = useState(false);

  useEffect(() => {
    setItems([...props.itemList]);
    setCount(parseInt(props.numOfItems, 10));
    setStartIndex(0)
  }, [props.itemList]);
  useEffect(() => {
    let numOfItemsToShow = [];
    console.log('props.itemList',props.itemList)
    const maxLimit = startIndex + count;
    for (let i = startIndex; i < maxLimit; i++) {
      numOfItemsToShow.push(props.itemList[i]);
    }
    console.log(numOfItemsToShow)
    setItemsToShow([...numOfItemsToShow]);
    if (startIndex === 0) {
      setDisabledChevronRight(true);
    } else {
      setDisabledChevronRight(false);
    }
    if (startIndex === items.length - count) {
      setDisabledChevronLeft(true);
    } else {
      setDisabledChevronLeft(false);
    }
  }, [props.itemList, count, startIndex]);
  const prev = () => {
    document.getElementsByClassName('.carousel-items')[0].classList.remove('.slide-forward')
    document.getElementsByClassName('.carousel-items')[0].classList.add('.slide-backward')
    if (startIndex > 0) {
      setStartIndex(startIndex - 1);
    }
  };
  const next = (e) => {
    document.getElementsByClassName('.carousel-items')[0].classList.remove('.slide-backward')
    document.getElementsByClassName('.carousel-items')[0].classList.add('.slide-forward')
    if (startIndex < items.length - count) {
      setStartIndex(startIndex + 1);
    }
  };

  return (
    <div className="carousel-container">
      <div onClick={prev} className="arrow-circle">
        <Chevron disabled={disableChevronRight} chevronDirection="right" />
      </div>
      <div className="carousel-items">
        {itemsToShow.length > 0
          ? itemsToShow.map((itemToShow) => {
              return <ProductCard product={itemToShow} />;
            })
          : null}
      </div>
      <div onClick={next} className="arrow-circle">
        <Chevron disabled={disableChevronLeft} chevronDirection="left" />
      </div>
    </div>
  );
};

export default Carousel;
