import React, { useEffect, useState } from "react";
import "./styles.css";
import Carousel from "./Carousel";
import items from "./Items";
import { getUniqueElements } from "./HelperFunctions.js";
const App = () => {
  const [selectedValue, setSelectedValue] = useState("default");
  const [itemList, setItemList] = useState(items);
  const categorySet = getUniqueElements(items, "category");

  const handleSelect = (e) => {
    setSelectedValue(e.target.value);
  };
  useEffect(() => {
    if (selectedValue === "default") {
      setItemList([...items]);
    }
    if (selectedValue !== "default") {
      const filteredItems = items.filter(
        (item) => item.category === selectedValue
      );
      setItemList([...filteredItems]);
    }
  }, [selectedValue]);
  return (
    <div className="App">
      <div class="category-filter">
      <label>Filter By Category:</label>
      <select onChange={(e) => handleSelect(e)} id="dropdown">
        <option value="default">All</option>
        {categorySet.map((category) => {
          return <option value={category}>{category}</option>;
        })}
      </select>
      </div>
      <Carousel numOfItems="3" itemList={itemList}></Carousel>
    </div>
  );
};
export default App;
