export const getUniqueElements = (someArray, key) => {
  var uniqueElemSet = new Set(
    someArray.map((someArrayElem) => someArrayElem[key])
  );
  return [...uniqueElemSet];
};
var _requestAnimationFrame = (function (win, t) {
  return (
    win["webkitR" + t] ||
    win["r" + t] ||
    win["mozR" + t] ||
    win["msR" + t] ||
    function (fn) {
      setTimeout(fn, 60);
    }
  );
})(window, "equestAnimationFrame");

function animate(item) {
  var duration = 1000 * item.time,
    end = +new Date() + duration;

  var step = function () {
    var current = +new Date(),
      remaining = end - current;

    if (remaining < 60) {
      item.run(1); //1 = progress is at 100%
      return;
    } else {
      var rate = 1 - remaining / duration;
      item.run(rate);
    }

    _requestAnimationFrame(step);
  };
  step();
}

export function goLeft() {
  animate({
    time: 0.5,
    run: function (rate) {
      document.getElementById("mover").style.left =
        rate * (300 - 150) + 150 + "px";
    }
  });
}

export function goRight() {
  animate({
    time: 0.5,
    run: function (rate) {
      document.getElementById("mover").style.left = 300 - rate * 150 + "px";
    }
  });
}
